def atlas_doc_to_text(atlas_doc_json):
    def extract_text(content):
        text = []
        if isinstance(content, list):
            for item in content:
                text.extend(extract_text(item))
        elif isinstance(content, dict):
            if content.get('type') == 'text':
                text.append(content.get('text', ''))
            elif content.get('type') in ['heading', 'paragraph']:
                text.extend(extract_text(content.get('content', [])))
            elif content.get('type') == 'bulletList':
                for item in content.get('content', []):
                    text.append('• ' + ' '.join(extract_text(item.get('content', []))))
            elif content.get('type') == 'table':
                for row in content.get('content', []):
                    row_text = []
                    for cell in row.get('content', []):
                        row_text.extend(extract_text(cell.get('content', [])))
                    text.append(' | '.join(row_text))
        return text

    try:
        doc = json.loads(atlas_doc_json)
        return '\n'.join(extract_text(doc.get('content', [])))
    except json.JSONDecodeError:
        return "Error: Invalid JSON format"

def construct_link(page_content_list, page_id):

    for page in page_content_list: 
        if page["id"] == page_id:
            link = page["_links"]["base"] + page['_links']["webui"]
            break
    return link

    
def construct_data(page_content_list, workspace, space_list, chunk_size = 1500):
    total_chunks = 0
    chunk_dict = {}
    for s in workspace: 
        print(s["workspaceName"])
        for space in space_list: 
            # print("ID", space['id'])
            # raw_data += f"\nWORKSPACE NAME: {s['workspaceName']}\n"
            workspace_name = s["workspaceName"]
            # print("SPACE NAME: ", space['name'])
            # raw_data += f"SPACE NAME: {space['name']}\n"
            space_name = space["name"]
            for page in page_content_list:
                raw_data = ""
                chunk_texts = []
                chunks = []

                if page['spaceId'] == space['id']:
                    print(page["title"]) 
                    print("PAGE LINK", construct_link(page_content_list, page["id"]))

                    atlas_doc_content = page["body"]["atlas_doc_format"]["value"]
                    readable_content = atlas_doc_to_text(atlas_doc_content)
                    print(readable_content)
                    raw_data += "CONTENT: ```\n"
                    raw_data += readable_content + '\n'
                    raw_data += "```\n"
                    raw_data += "--------------------------------------"
                    current_chunk = ""
                    sentences = sent_tokenize(raw_data) 
                    for sentence in sentences:
                        if len(current_chunk) + len(sentence) <= chunk_size:
                            current_chunk += sentence + " "
                            print("HEREE++++++++++++++++")
                        else:
                            if current_chunk:
                                chunks.append(current_chunk.strip())
                            current_chunk = sentence + " "
                            print("here====================")
                    if current_chunk:
                        chunks.append(current_chunk.strip())
                        print("aaaaaaaaaaaaaaaaa")
                    for i, chunk in enumerate(chunks, 1):
                        # Create a metadata string that includes the source and file name.
                        metadata_str = (
                            f"Workspace name: {workspace_name}\n"
                            f"Space name: {space_name}\n"
                            f"Document link: {construct_link(page_content_list, page['id'])}\n"
                            f"Title: {page['title']}\n"
                            f"Chunk: {i}/{len(chunks)}"
                        )
                        # Combine the metadata with the chunk content.
                        combined_chunk = f"{metadata_str}\n\n{chunk}"
                        chunk_texts.append(combined_chunk)
                    chunk_dict.update({page["id"]: chunk_texts})

    print("--------------------RAW DATA ------------------------") 
    print(chunk_dict) 
    print("--------------------ENNNNNNDDDDDDDD--------------------")

    f = open("data_to_embed.txt", "w")
    f.write(str(chunk_dict))
    f.close()

    return chunk_dict





async def embed_item_list_to_pinecone(page_content_list, workspace, space_list, organization_id, user_id):
    chunks = construct_data(page_content_list, workspace, space_list)
    print("chunks", chunks)
    print("========================ITEM LIST ==================================")
    if chunks is None:
        raise HTTPException(status_code=400, detail="No item list found.")

    for key, values in chunks.items(): 
        for i, value in enumerate(values):
            PineconeClient().CreateIndexNameSpaceForUniversalSearch(
                index_name=settings.PINECONE_PROJECT_INDEX,
                input_data=value,
                namespace=f"{organization_id}_universal_search",
                namespace_ids=[str(key)+"_"+str(i+1)],
                replace=True,
                user_id = user_id,
                meta_key="confluence_file_id"
            )
            print("VALue: ", value)
    print("OKKKKAAAAAAAAAAAYYYYYYY DONEEEEEEEEEE") 

    
